import { Line, Area, Column, Bar } from "@ant-design/charts";
import { Button, Col, DatePicker, Row } from "antd";
import React, { useState } from "react";
function Dashboard() {
  const { RangePicker } = DatePicker;
  const [date, setDate] = useState({
    fromDate: Date.now(),
    toDate: Date.now(),
  });

  const data = [
    { year: "1991", value: 3 },
    { year: "1992", value: 5 },
    { year: "1994", value: 2 },
    { year: "1996", value: 1 },
    { year: "1997", value: 4 },
    { year: "1998", value: 6 },
    { year: "1999", value: 7 },
    { year: "1999", value: 8 },
  ];

  const config = {
    data,
    width: 300,
    height: 150,
    autoFit: false,
    xField: "year",
    yField: "value",
    point: {
      size: 5,
      shape: "diamond",
    },
    label: {
      style: {
        fill: "#aaa",
      },
    },
  };

  const configMain = {
    data,
    with: 750,
    height: 320,
    autoFit: false,
    xField: "year",
    yField: "value",
    point: {
      size: 5,
      shape: "diamond",
    },
    label: {
      style: {
        fill: "#aaa",
      },
    },
  };

  const onChangeRange = (dates, datestrings) => {
    setDate({...date, fromDate: dates, toDate:datestrings});
  };
console.log(date);
  const onClickOne = (e) => {
    setDate({...date, fromDate: '01/01/1991', toDate: '01/01/1995'})
  };
  console.log(date);
  const onClickTwo = (e) => {
    console.log(e);
  };

  return (
    <div className="dashboard">
      <div className="date-picker-place">
        <Row>
          <Col span={6} className="col-date-picker-place">
            <Line {...config} />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Area {...config} />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Column {...config} />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Bar {...config} />
          </Col>
        </Row>
        <Row className="chart-main">
          <Col span={16} className="main-chart-dashboard">
            <h3>Dashboard-main</h3>
            <Column {...configMain} />
          </Col>
          <Col span={8} className="main-chart-dashboard">
            <Button type="link" onClick={onClickOne}>
              91-95
            </Button>
            <Button type="link" onClick={onClickTwo}>
              95-99
            </Button>
            <RangePicker onChange={onChangeRange} format="MM-DD-YYYY" />
          </Col>
        </Row>
      </div>
    </div>
  );
}
export default Dashboard;
