import { Table, Tag } from "antd";
import axios from "axios";
import React, { useEffect, useState } from "react";

function Product() {
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("/product?q=rose&limit=25&page=1")
      .then((response) => {
        if (response.status === 200) {
          setProduct(response.data);
          setLoading(false);
        } else {
          console.log("Connect fail");
        }
      })
      .catch(function (error) {
        console.log(error);
      });

    return () => {};
  }, []);

  const conlumn = [
    {
      title: "Name",
      dataIndex: "name",
      key: "name",
    },
    {
      title: "Brand",
      dataIndex: "brand",
      key: "brand",
    },
    {
      title: "Ingredient",
      dataIndex: "ingredient_list",
      key: "ingredient_list",
      render: (item) => {
        return <div className="ingredient-list-table">{item.toString()}</div>;
      },
    },
    {
      title: "Tags",
      key: "tags",
      dataIndex: "ingredient_list",
      render: (tags) => (
        <>
          {tags.map((tag) => {
            let color = tag.length > 5 ? "geekblue" : "green";
            if (tag === "loser") {
              color = "volcano";
            }
            return (
              <Tag color={color} key={tag}>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
  ];
  return (
    <div>
      <Table
        columns={conlumn}
        dataSource={product}
        className="table-product"
      ></Table>
    </div>
  );
}
export default Product;
