import { Card, Col, Pagination, Row, Select } from "antd";
import axios from "axios";
import React, { useEffect, useState } from "react";
import product_image from "../../asset/frontend/product-image.jpg";
function ListProduct() {
  const [product, setProduct] = useState([]);
  const { Meta } = Card;
  const [current, setCurrent] = useState(1);
  const numEachPage = 30;
  const [brand, setBrand] = useState([]);
  const [state, setState] = useState({
    min: 0,
    max: numEachPage,
  });

  const options = [];
  const getBrand = () => {
    for (let i = 0; i < product.length; i++) {
      options.push(product[i].brand);
    }
  };
  getBrand();
  const new_options = [...new Set(options)];

  const filteredOptions = new_options.filter((o) => !brand.includes(o));
  const handleChange = (value, pageSize) => {
      if (pageSize !== numEachPage) {
        setState({
          min: (value - 1) * pageSize,
          max: value * pageSize,
        });
      }else{
        setState({
            min: (value - 1) * numEachPage,
            max: value * numEachPage,
          });
      }
      setCurrent(value);
  };
  const handleChangeBrand = (value) => {
    setBrand(value);
    if (brand.length !== 0){
        handleChange(1, product.length)
    }else{
        handleChange()
    }
  };
  useEffect(() => {
    axios
      .get("/product?q=rose&limit=25&page=1")
      .then((response) => {
        if (response.status === 200) {
          setProduct(response.data);
        } else {
          console.log("Connect fail");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  return (
    <div className="site-card-wrapper">
      <Row>
        <Col span={4}></Col>
        <Col span={20}>
          <Select
            placeholder="Inserted brand"
            value={brand}
            onChange={handleChangeBrand}
            style={{ width: "100%", marginBottom: 30 }}
          >
              <Select.Option key="0" value="">
                All
              </Select.Option>
            {filteredOptions.map((item) => (

              <Select.Option key={item} value={item}>
                {item}
              </Select.Option>
            ))}
          </Select>
          <Row gutter={16}>
            {product &&
              product.length > 0 &&
              product
                .slice(state.min, state.max)
                .filter((e) => {
                  if (e.brand.includes(brand)) {
                    return e;
                  } else {
                    return e.brand.includes(brand);
                  }
                })
                .map((item) => {
                  return (
                    <Col span={4} style={{ marginBottom: 30 }} key={item.id}>
                      <Card
                        title={item.name}
                        bordered={false}
                        children
                        className="card-product"
                      >
                        <div className="product-image-area">
                          <img
                            alt=""
                            src={product_image}
                            className="product-image"
                          />
                        </div>
                        <Meta
                          title={item.brand}
                          description={
                            <div className="description-product">
                              {item.ingredient_list.toString()}
                            </div>
                          }
                        />
                      </Card>
                    </Col>
                  );
                })}
          </Row>
          <div className="paginate-align">
            {brand.length === 0 ? (
              <Pagination
                current={current}
                pageSizeOptions={[10, 20, 30, 50, 100]}
                size="large"
                total={product.length}
                showSizeChanger
                showQuickJumper
                defaultPageSize={numEachPage}
                onChange={handleChange}
              />
            ) : (
              ""
            )}
          </div>
        </Col>
      </Row>
    </div>
  );
}
export default ListProduct;
