import { UnorderedListOutlined } from "@ant-design/icons/lib/icons";
import { List, Button, Skeleton, Avatar } from "antd/lib/form/Form";
import React, { useEffect, useState } from "react";

function Slidebar() {
  const count = 3;
  const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;

  const [state, setState] = useState({
    initLoading: true,
    loading: false,
    data: [],
    list: [],
  });

  useEffect =
    (() => {
      axios(fakeDataUrl)
        .then((res) => res.json())
        .then((res) => {
          this.setState({
            initLoading: false,
            data: res.results,
            list: res.results,
          });
        });
    },
    []);

  const onLoadMore = () => {
    setState({
      loading: true,
      list: state.data.concat(
        [...new Array(count)].map(() => ({
          loading: true,
          name: {},
          picture: {},
        }))
      ),
    });
    fetch(fakeDataUrl)
      .then((res) => res.json())
      .then((res) => {
        const data = state.data.concat(res.results);

        setState(
          {
            data,
            list: data,
            loading: false,
          },
          () => {
            // Resetting window's offsetTop so as to display react-virtualized demo underfloor.
            // In real scene, you can using public method of react-virtualized:
            // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized
            window.dispatchEvent(new Event("resize"));
          }
        );
      });
  };
  const { initLoading, loading, list } = state;
  const loadMore =
    !initLoading && !loading ? (
      <div
        style={{
          textAlign: "center",
          marginTop: 12,
          height: 32,
          lineHeight: "32px",
        }}
      >
        <Button onClick={onLoadMore}>loading more</Button>
      </div>
    ) : null;

  return (
    <div className="slide-bar">
      <div className="slide-bar-category">
        <div className="slide-bar-category-title">
          <UnorderedListOutlined /> All Category
        </div>
        <div className="slide-bar-category-list">
          <List
            className="demo-loadmore-list"
            loading={initLoading}
            itemLayout="horizontal"
            loadMore={loadMore}
            dataSource={list}
            renderItem={(item) => (
              <List.Item
                actions={[
                  <a key="list-loadmore-edit">edit</a>,
                  <a key="list-loadmore-more">more</a>,
                ]}
              >
                <Skeleton avatar title={false} loading={item.loading} active>
                  <List.Item.Meta
                    avatar={<Avatar src={item.picture.large} />}
                    title={<a href="https://ant.design">{item.name.last}</a>}
                    description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                  />
                  <div>content</div>
                </Skeleton>
              </List.Item>
            )}
          />
        </div>
      </div>
    </div>
  );
}

export default Slidebar;
