import { Col, Image, Row, Spin } from "antd";
import axios from "axios";
import React, { useEffect, useState } from "react";
import product_image from "../../asset/frontend/product-image.jpg";

function Default(props) {
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const product_id = props.match.params.id;
    axios.get(`/products/${product_id}`).then((res) => {
      if (res.status === 200) {
        setProduct(res.data);
        setLoading(false)
      } else {
        console.log("Connect fails");
      }
    });
  }, [props.match.params.id]);

  const LoadingPage = () => {
    return (
      <div className="loading-screen">
        {" "}
        <Spin /> <span>Loading...</span>
      </div>
    );
  };

  return (
    <div className="product-detail">
      <Row>
        <Col span={12} className="center-text">
          <div className="image-product-detail">
            <Image
            width={500}
              src={product_image}
            />
          </div>
        </Col>
        <Col span={12}>
            <div className="product-item-content">
                <div className="product-item-title">{product.name}</div>
                <div className="product-item-brand">Brand: {product.brand}</div>
                <div className="product-item-ingredient">{product.ingredient_list}</div>
            </div>
        </Col>
      </Row>
    </div>
  );
}
export default Default;
