export class Deferred {
  constructor() {
    var _this = this;

    this.isResolved = false;
    this.isRejected = false;
    this.promise = new Promise((resolve, reject) => {
      this.resolve = function () {
        _this.isResolved = true;
        resolve(...arguments);
      };

      this.reject = function () {
        _this.isRejected = true;
        reject(...arguments);
      };
    });
  }

}
