import { Line, Area, Column, Bar } from "@ant-design/charts";
import { SearchOutlined } from "@ant-design/icons/lib/icons";
import { Button, Col, DatePicker, Row } from "antd";
import React from "react";
function Dashboard() {
  const { RangePicker } = DatePicker;
  const data = [
    { year: "1991", value: 3 },
    { year: "1992", value: 5 },
    { year: "1994", value: 2 },
    { year: "1996", value: 1 },
    { year: "1997", value: 4 },
    { year: "1998", value: 6 },
    { year: "1999", value: 7 },
    { year: "1999", value: 8 },
  ];

  const config = {
    data,
    width: 300,
    height: 150,
    autoFit: false,
    xField: "year",
    yField: "value",
    point: {
      size: 5,
      shape: "diamond",
    },
    label: {
      style: {
        fill: "#aaa",
      },
    },
  };

  const configMain = {
    data,
    with: 750,
    height: 320,
    autoFit: false,
    xField: "year",
    yField: "value",
    point: {
      size: 5,
      shape: "diamond",
    },
    label: {
      style: {
        fill: "#aaa",
      },
    },
  };

  let chart;
  return (
    <div className="dashboard">
      <div className="date-picker-place">
        <Row>
          <Col span={6} className="col-date-picker-place">
            <Line
              {...config}
              onReady={(chartInstance) => (chart = chartInstance)}
            />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Area
              {...config}
              onReady={(chartInstance) => (chart = chartInstance)}
            />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Column
              {...config}
              onReady={(chartInstance) => (chart = chartInstance)}
            />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Bar {...config} />
          </Col>
        </Row>
        <Row className="chart-main">
          <Col span={16} className="main-chart-dashboard">
            <h3>Dashboard-main</h3>
            <Column
              {...configMain}
              onReady={(chartInstance) => (chart = chartInstance)}
            />
          </Col>
          <Col span={8} className="main-chart-dashboard">
            <Button type="link">1</Button>
            <Button type="link">2</Button>
            <Button type="link">3</Button>
            <RangePicker onChange={onChangeRange}/>
          </Col>
        </Row>
      </div>
    </div>
  );
}
export default Dashboard;
