import { Table, Tag } from "antd";
import axios from "axios";
import React, { useEffect, useState } from "react";

function Product() {
  const [product, setProduct] = useState([]);
  const [state, setState] = useState({
    selectedRowKeys: []
  });

  const onSelectChange = (value) => {
    console.log("select: ", value);
    setState(state.selectedRowKeys);
  };
  const {selectedRowKeys} = state
  const rowSelection = {
    selectedRowKeys,
    onChange: onSelectChange,
    selections: [
      Table.SELECTION_ALL,
      Table.SELECTION_INVERT,
      Table.SELECTION_NONE,
      {
        key: "odd",
        text: "Select Odd Row",
        onSelect: changaleRowKeys => {
          let newSelectedRowKeys = [];
          newSelectedRowKeys = changaleRowKeys.filter((key, index) => {
            if (index % 2 !== 0) {
              return false;
            }
            return true;
          });
          setState({selectedRowKeys :newSelectedRowKeys});
        },
      },
      {
        key: "even",
        text: "Select Even Row",
        onSelect: changaleRowKeys => {
          let newSelectedRowKeys = [];
          newSelectedRowKeys = changaleRowKeys.filter((key, index) => {
            if (index % 2 !== 0) {
              return true;
            }
            return false;
          });
          setState({selectedRowKeys :newSelectedRowKeys});
        },
      },
    ],
  };

  useEffect(() => {
    axios
      .get("/product?q=rose&limit=25&page=1")
      .then((response) => {
        if (response.status === 200) {
          setProduct(response.data);
        } else {
          console.log("Connect fail");
        }
      })
      .catch(function (error) {
        console.log(error);
      });

    return () => {};
  }, []);

  const conlumn = [
    {
      title: "Name",
      dataIndex: "name",
      key: "name",
    },
    {
      title: "Brand",
      dataIndex: "brand",
      key: "brand",
    },
    {
      title: "Ingredient list",
      key: "tags",
      dataIndex: "ingredient_list",
      render: (tags) => (
        <>
          <div className="ingredient-list-table">{tags.toString()}</div>
        </>
      ),
    },
    {
      title: "Quality",
      key: "quantity",
      dataIndex: "ingredient_list",
      render: (tags) => (
        <>
          {tags.length > 50 ? (
            <Tag color="green">GOOD</Tag>
          ) : (
            <Tag color="geekblue">NORMAL</Tag>
          )}
        </>
      ),
    },
  ];
  return (
    <div>
      <Table
        rowSelection={rowSelection}
        columns={conlumn}
        dataSource={product}
        className="table-product"
      ></Table>
    </div>
  );
}
export default Product;
