import { Line, Area, Column, Bar } from "@ant-design/charts";
import { Button, Col, DatePicker, Row } from "antd";
import React, { useMemo, useState } from "react";
import moment from "moment";
import { range, set } from "lodash";
import useModal from "antd/lib/modal/useModal";
function Dashboard() {
  const { RangePicker } = DatePicker;
  const [date, setDate] = useState({
    from: moment(),
    to: moment()
  });
  const dateFormat = "MM-DD-YYYY";

  const data = [
    { year: "1991", value: 3 },
    { year: "1992", value: 5 },
    { year: "1994", value: 2 },
    { year: "1996", value: 1 },
    { year: "1997", value: 4 },
    { year: "1998", value: 6 },
    { year: "1999", value: 7 },
    { year: "1999", value: 8 },
  ];

  const config = {
    data,
    width: 300,
    height: 150,
    autoFit: false,
    xField: "year",
    yField: "value",
    point: {
      size: 5,
      shape: "diamond",
    },
    label: {
      style: {
        fill: "#aaa",
      },
    },
  };

  const configMain = {
    data,
    with: 750,
    height: 320,
    autoFit: false,
    color: "#597ef7",
    xField: "year",
    yField: "value",
    point: {
      size: 5,
      shape: "diamond",
    },
    label: {
      style: {
        fill: "#aaa",
      },
    },
  };

  const onChangeRange = (dates, datestrings) => {
    // console.log({ ...date, range: dates})
    const startTime = moment(dates[0]._d).format(dateFormat);
    const endTime = moment(dates[1]._d).format(dateFormat);
    setDate({...date, from: startTime, to: endTime})
  };

  const onClickOne = (e) => {
    setDate({...date, from: "01-01-1991", to: "01-01-1995"})
  };

  const onClickTwo = (e) => {
    setDate({...date, from: "01-01-1995", to: "01-01-1999"})
  };

  const range = useMemo(()=>{
    const range = [];
    range.push(
      date.from, date.to
    )
    return range
  },[date])

  console.log(range);
  // const fromDatetoDate = useMemo(() => {
  //   const fromDate = moment(date.toDate).format("DD-MM-YYYY");
  //   const toDate = moment(date.fromDate).format("DD-MM-YYYY");
  //   const fromDatetoDate = [];
  //   fromDatetoDate.push(fromDate);
  //   fromDatetoDate.push(toDate);
  //   return fromDatetoDate;
  // }, [date]);

  const lineChart = { ...config, color: "#ff4d4f" };
  const areaChart = { ...config, color: "#7cb305" };
  const comlumnChart = { ...config, color: "#08979c" };
  const barChart = { ...config, color: "#531dab" };

  return (
    <div className="dashboard">
      <div className="date-picker-place">
        <Row>
          <Col span={6} className="col-date-picker-place">
            <Line {...lineChart} />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Area {...areaChart} />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Column {...comlumnChart} />
          </Col>
          <Col span={6} className="col-date-picker-place">
            <Bar {...barChart} />
          </Col>
        </Row>
        <Row className="chart-main">
          <Col span={16} className="main-chart-dashboard">
            <h3>Dashboard-main</h3>
            <Column {...configMain} />
          </Col>
          <Col span={8} className="main-chart-dashboard">
            <Button type="link" onClick={onClickOne}>
              91-95
            </Button>
            <Button type="link" onClick={onClickTwo}>
              95-99
            </Button>
            <RangePicker onChange={onChangeRange} format="MM-DD-YYYY"/>
          </Col>
        </Row>
      </div>
    </div>
  );
}
export default Dashboard;
