import { Affix, Layout, Menu, Dropdown, Modal, Form, Input } from "antd";
import React, { useState } from "react";
import "antd/dist/antd.css";
import { Link } from "react-router-dom";
import { DownOutlined } from "@ant-design/icons/lib/icons";

function Header() {
  const { Header } = Layout;
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const menu = (

    <Menu>
      <Menu.Item>
        <Link onClick={showModal}>Navigate to login</Link>
        <Modal
          title="Login/Sign in"
          visible={isModalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
        >
          <Form>
            <Form.Item
            label="Username"
            name="username"
            rules={[
              {
                required: true,
                message: 'Please input your username!',
              },
            ]}
            >
              <Input/>
            </Form.Item>
            <Form.Item>
              <Input/>
            </Form.Item>
          </Form>
        </Modal>
      </Menu.Item>
      <Menu.Item danger>Navigate to admin</Menu.Item>
    </Menu>
  );

  return (
    <Affix>
      <Layout>
        <Header className="header">
          <div className="logo">
            <Link to="/">
              <h2 className="logo-text">BEAUTY-G</h2>
            </Link>
          </div>
          <Menu theme="dark" mode="horizontal" className="menu-item-list">
            <Menu.Item key="1" className="menu-item">
              <Link to="/">HOME</Link>
            </Menu.Item>
            <Menu.Item key="2" className="menu-item">
              ABOUT US
            </Menu.Item>
            <Menu.Item key="3" className="menu-item">
              <Link to="/list-product">PORTFOLIO</Link>
            </Menu.Item>
            <Menu.Item key="4" className="menu-item">
              SERVICES
            </Menu.Item>
            <Menu.Item key="5" className="menu-item">
              <Dropdown overlay={menu}>
                <Link
                  className="ant-dropdown-link"
                  onClick={(e) => e.preventDefault()}
                  to="/"
                >
                  NAVIGATE <DownOutlined />
                </Link>
              </Dropdown>
            </Menu.Item>
          </Menu>
        </Header>
      </Layout>
    </Affix>
  );
}
export default Header;
