import { Card, Col, Pagination, Row } from "antd";
import axios from "axios";
import React, { useEffect, useState } from "react";
import product_image from "../../asset/frontend/product-image.jpg";
function ListProduct() {
  const [product, setProduct] = useState([]);
  const { Meta } = Card;
  const [current, setCurrent] = useState(1);
  const [numEachPage, setNumEachPage] = useState(24);
  const [state, setState] = useState({
    min: 0,
    max: numEachPage,
  });
  console.log({ current });

  const handleChange = (value) => {
    setState({
      min: (value - 1) * numEachPage,
      max: value * numEachPage,
    });
  };

  const handleChangePageSize= (value) => {
      setState({
        min: (value - 1) * numEachPage,
        max: value * numEachPage,
      })
  }

  useEffect(() => {
    axios
      .get("/product?q=rose&limit=25&page=1")
      .then((response) => {
        if (response.status === 200) {
          setProduct(response.data);
        } else {
          console.log("Connect fail");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  return (
    <div className="site-card-wrapper">
      <Row>
        <Col span={4}></Col>
        <Col span={20}>
          <Row gutter={16}>
            { product &&
              product.length > 0 &&
              product.slice(state.min, state.max).map((item) => {
                return (
                  <Col span={4} style={{ marginBottom: 30 }} key={item.id}>
                    <Card
                      title={item.name}
                      bordered={false}
                      children
                      className="card-product"
                    >
                      <div className="product-image-area">
                        <img
                          alt=""
                          src={product_image}
                          className="product-image"
                        />
                      </div>
                      <Meta
                        title={item.brand}
                        description={
                          <div className="description-product">
                            {item.ingredient_list.toString()}{" "}
                          </div>
                        }
                      />
                    </Card>
                  </Col>
                );
              })}
          </Row>
          <div className="paginate-align">
            <Pagination
              current={current}
              pageSize={numEachPage}
              size="large"
              total={product.length}
              showQuickJumper
              onChange={(page, newPageSize)=>{
                  setNumEachPage(newPageSize)
                  setCurrent(numEachPage !== newPageSize ? 1 : page)
              }}
              onShowSizeChange={handleChangePageSize}
            />
          </div>
        </Col>
      </Row>
    </div>
  );
}
export default ListProduct;
