            //         se_permission = 'level_2'
            //     }
            //     localStorage.setItem('se_permission', se_permission);
            // }
            // localStorage.removeItem('token');
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        checkPermission();
    }, [])

    return (
        <>
            {localStorage.getItem('se_permission') !== '' ? isOpen ?
                <InputMessage
                    isOpen={isOpen}
                    inputRef={inputRef}
                    submitMessage={submitMessage}
                    isLoadingRespons={isLoadingRespons}
                    setIsOpen={setIsOpen}
                    setSubmitMessage={setSubmitMessage}
                    setIsLoadingRespons={setIsLoadingRespons}
                />
                :
                <IconChatBot
                    setIsOpen={setIsOpen} />
                : null
            }
        </>
    );
};

export default App;
