import React, { useEffect, useRef, useState } from 'react';
import IconChatBot from './ChatBot/IconChatbot';
import InputMessage from './ChatBot/InputMessage';
import * as ChatServices from '../app/services';

import '../styles/index.scss';

const App = () => {
    const inputRef = useRef(null);
    const [isOpen, setIsOpen] = useState<boolean>(false);
    const [submitMessage, setSubmitMessage] = useState<any>([]);
    const [isLoadingRespons, setIsLoadingRespons] = useState<boolean>(false);

    const checkPermission = async () => {
        try {
            const resp = await ChatServices.check_permission({ Token: localStorage.getItem('token') });
            console.log(resp);
        } catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        checkPermission();
    }, [])
    return (
        <>
            {localStorage.getItem('se_permission') || isOpen ?
                <InputMessage
                    isOpen={isOpen}
                    inputRef={inputRef}
                    submitMessage={submitMessage}
                    isLoadingRespons={isLoadingRespons}
                    setIsOpen={setIsOpen}
                    setSubmitMessage={setSubmitMessage}
                    setIsLoadingRespons={setIsLoadingRespons}
                />
                :
                <IconChatBot
                    setIsOpen={setIsOpen} />
            }
        </>
    );
};

export default App;
